import React, { useState } from 'react'
import { Dropdown, FormInput, Grid, Input } from 'semantic-ui-react'
import img from '../../img/slide2.jpg'
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import { MoreVert, Share } from '@material-ui/icons'
import { Modal } from 'semantic-ui-react'
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';
import { Box, IconButton, Popover, Typography } from '@material-ui/core'

const InspImage = (props) => {
    const [currentvalue, setcurrentvalue] = useState("Select Board")
    const [openmodel, setopenmodel] = useState(false)
    const [placeholder, setplaceholder] = useState('Choose Board')
    // const [overlayoptions, setoverlayoptions] = useState(initialoptions)
    const [initialoptions, setinitialoptions] = useState([
        { key: 1, text: 'Bedroom', value: 1 },
        { key: 2, text: 'Drawing Room', value: 2 },
        { key: 3, text: 'Washroom/Toilet', value: 3 },
        { key: 4, text: 'Study Room', value: 4 },
        { key: 5, text: 'kitchen ', value: 5 },
        { key: 6, text: 'Balcony', value: 6 },
    ]);

    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
  
    const handleClose = () => {
      setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
  const id = open ? 'simple-popover' : undefined;

    const handleopenmodel = (img) => {
        setopenmodel(true)


    }
    const handleclosemodel = () => {
        setopenmodel(false)
    }
    const handleselectchange = (e) => {
        setcurrentvalue(e.target.value)
    }
    const handleboardaddition = (e, { value }) => {
        setinitialoptions([
            {
                key: initialoptions.length,
                text: value,
                value: initialoptions.length
            }, ...initialoptions
        ]);
        setcurrentvalue(value)
        setplaceholder(value)
    }
    return (
        <div>
            <div  className="img_box">
                <img onClick={handleopenmodel} src={props.img} alt="Avatar" class="image" style={{ width: '100%' }} /><center></center>
                <div className="img_overlay">
     
                    <div  onClick={handleClick} className="overlay">Save</div>
          
          
        
                    <Dropdown fluid text={currentvalue}>
    <Dropdown.Menu>
      <Dropdown.Item text='New' />
      <Dropdown.Item onc text='Open...' description='ctrl + o' />
      <Dropdown.Item text='Save as...' description='ctrl + s' />
      <Dropdown.Item text='Rename' description='ctrl + r' />
   
     
      <Dropdown.Divider />
      <Dropdown.Item icon='add' text='Create New board...' />
      
    </Dropdown.Menu>
  </Dropdown>

                


                </div>
                <div className="img_overlay2">
                    <div  className="img_overlayshare"><Share /></div>
                    <div className="img_overlaylike"> <FavoriteBorderIcon /></div>


                </div>
            </div>

            <Modal
                dimmer='blurring'
                className="model"
                open={openmodel}
                size='small'
                onClose={handleclosemodel}
            >
                <div className="model_btn">
                    <div>
                        <IconButton><SwapHorizIcon /></IconButton>
                        <IconButton>    <FavoriteBorderIcon /></IconButton>

                        <IconButton> <Share /></IconButton>
                    </div>
                    <div className='model_savebtn'>
                        
                            <Dropdown
                        options={initialoptions}
                        placeholder={placeholder}
                        search
                        selection
                        fluid
                        allowAdditions
                        value={initialoptions[0]}
                        onAddItem={handleboardaddition}
                        onChange={handleselectchange}/>
                        <div className="overlay">Save</div>
                    </div>
                </div>
                <img src={props.img} alt="" />

            </Modal>
           
        </div>
    )
}

export default InspImage
