import React from 'react'
import Pagination from '@material-ui/lab/Pagination';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import { Share } from '@material-ui/icons'
import { IconButton } from '@material-ui/core'
import { Divider, Icon, Search } from 'semantic-ui-react'
import img from '../../img/bg1.jpeg'
import { useHistory } from 'react-router-dom';
import { propTypes } from 're-carousel';

const BlogComponent = (props) => {
            const auther = props.auther;
            const date = props.date;
            const description = props.description;
            const view = props.view;
            const comment = props.comment;
            
            const history = useHistory();
            
   const hanldeReadMore=(title)=>{
             history.push({
                 pathname: `/blog/${title}`,
                state:{
                    auther:auther,
                    date : date,
                    view : view,
                    
                }
                })
   }


    return (
        <div>
              <div className="blog">
                            <div className="blogimg_box">
                                <img src={img} alt="" />
                                <div className="img_overlay2">
                                    <IconButton><SwapHorizIcon /></IconButton>
                                    <IconButton>    <FavoriteBorderIcon /></IconButton>
                                    <IconButton> <Share /></IconButton>
                                </div>
                                <div style={{ backgroundColor: 'red' }} className="img_overlay">{props.tag}</div>

                            </div>
                            <div className="title">{props.title}</div>
                            <div className="time"><Icon name='calendar alternate outline'></Icon> {props.date}</div>
                            <div className="description">{props.description}</div>
                            <Divider />
                            <div className="blog_btmbtn">
                                Eazinow <span onClick={()=>hanldeReadMore(props.title)}>Read More  <Icon name="caret right" />  </span>
                            </div>
                        </div>
        </div>
    )
}

export default BlogComponent
