                                    <IconButton><SwapHorizIcon /></IconButton>
                                    <IconButton>    <FavoriteBorderIcon /></IconButton>
                                    <IconButton> <Share /></IconButton>
                                </div>
                                <div style={{ backgroundColor: 'red' }} className="img_overlay">Construction</div>

                            </div>
                            <div className="title">{props.title}</div>
                            <div className="time"><Icon name='calendar alternate outline'></Icon> December 16,2020</div>
                            <div className="description">fvfysuifycufuiwe cwu rcwe rrcwec ree   er eru u g g  uiu f</div>
                            <Divider />
                            <div className="blog_btmbtn">
                                Eazinow <span onClick={()=>hanldeReadMore(props.title)}>Read More  <Icon name="caret right" />  </span>
                            </div>
                        </div>
        </div>
    )
}

export default BlogComponent
