import React, { useState } from 'react'
import './input-info.css'
import { Button, Dropdown, Grid,Input } from 'semantic-ui-react'
// import Select from '@material-ui/core/Select';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
// import MenuItem from '@material-ui/core/MenuItem';
import Popper from '@material-ui/core/Popper';
import Fade from '@material-ui/core/Fade';
const InputSection = () => {
     const [proptypevalue, setproptypevalue] = useState(" ")
     const [constypevalue, setconstypevalue] = useState(" ")
     const [openpopper, setopenpopper] = useState(false);
     const [ancher, setancher] = useState('currentTarget')

      const proptypeoptions = [
        { key: 1, text: 'Apartment', value: 1 },
        { key: 2, text: 'Villa', value: 2 },
        { key: 3, text: 'Builder Floor', value: 3 },
        { key: 4, text: 'Plot', value: 4 },
      ]
      const constypeoptions = [
        { key: 1, text: 'Home Renovation', value: 1 },
        { key: 2, text: 'Home Renovation with Civil work', value: 2 },
        { key: 3, text: 'Complete new Construction', value: 3 },
       
      ]
    const handleChangeprop = (event) => {
        setproptypevalue(event.target.value)
    };
    const handleChangecons = (event) => {
      setconstypevalue(event.target.value)
  };
const handlepopper=(e)=>{
  setopenpopper(true);
  setancher(e.currentTarget)

}
   
    return (
      <div className="main"> 

         <div className="head">
         <ul>
           <div className="brand">Eazinow</div>
           <div className='tag'>EXPLORE YOUR DREAM HOME HERE.</div>
           <div className='btns'>
           <button class='btn'>Home</button>
           <button class='btn'>Submit</button>
           </div></ul>
         </div>
        <div className='input-info'>
            {/* <Select className="select" size='x-large' placeholder='Select your country' options={countryOptions} /> */}
          <ul>
               
       
        <Grid columns={6}>
        <li>
          <Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          />
        </li>
        <li>
          <Dropdown
            onChange={handleChangecons}
            options={constypeoptions}
            placeholder='Construction Type'
            selection
            value={constypevalue}
          />
        </li>
        <li>
        <Input size="huge" type="text" placeholder='Area(sqft)' /><div className="area" >ft<span>2</span></div>
        </li>
        <li>
        <Input size="huge" type="text" placeholder='Pincode' />
        </li>
        <li>
        <div onClick={handlepopper} className='advance'>Advance
        <MoreVertIcon/>
        </div>   
        </li>
        <li>
          <button class='btn'>Submit</button>
        </li>
        </Grid>
   
          </ul>
       

        </div>
        <Popper timeout={350} anchorEl={ancher}   open={openpopper} transition>
  {({ TransitionProps }) => (
    <Fade {...TransitionProps} timeout={350}>
      <div className="popper"  >
      
      <Grid columns={4}> 
      <Grid.Row>
      <Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /><Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /><Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /><Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /></Grid.Row>
          <Grid.Row>
          <Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          />
          <Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          />
          <Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /><Dropdown
            onChange={handleChangeprop}
            openOnFocus
            options={proptypeoptions}
            placeholder='Property Type'
            selection
            value={proptypevalue}
          /></Grid.Row></Grid> </div>
    </Fade>
  )}
</Popper>
        </div>
    )
}

export default InputSection
